sourcesContent:
const speak = (text, language) => {
  if ("speechSynthesis" in window) {
    const speech = new SpeechSynthesisUtterance(text);
    speech.lang = language; // Set language dynamically

    // Get available voices
    const voices = window.speechSynthesis.getVoices();

    // Select voice based on language
    if (language === "hi-IN") {
      speech.voice = voices.find((v) => v.lang.includes("hi")) || null;
    } else if (language === "te-IN") {
      speech.voice = voices.find((v) => v.lang.includes("te")) || null;
    } else {
      speech.voice = voices.find((v) => v.lang.includes("en")) || null; // Default to English
    }

    speech.rate = 1;
    speech.pitch = 1;
    window.speechSynthesis.speak(speech);
  } else {
    console.error("Speech synthesis not supported in this browser.");
  }
};
