import React, { useState } from "react";
import { Container, Table, Card, Button } from "react-bootstrap";

const bankLoanOffers = [
  {
    bank: "HDFC Bank",
    interestRate: "7.5% - 10.5%",
    maxLoan: "‚Çπ50 Lakhs",
    tenure: "12 - 84 months",
    eligibility: "Salaried & Self-Employed",
  },
  {
    bank: "SBI Bank",
    interestRate: "8.0% - 11.0%",
    maxLoan: "‚Çπ30 Lakhs",
    tenure: "12 - 72 months",
    eligibility: "Salaried & Pensioners",
  },
  {
    bank: "ICICI Bank",
    interestRate: "7.8% - 10.2%",
    maxLoan: "‚Çπ40 Lakhs",
    tenure: "12 - 84 months",
    eligibility: "Salaried & Business Owners",
  },
  {
    bank: "Axis Bank",
    interestRate: "8.2% - 11.5%",
    maxLoan: "‚Çπ35 Lakhs",
    tenure: "12 - 60 months",
    eligibility: "Salaried & Self-Employed",
  },
];

const BankLoans = () => {
  const [selectedBank, setSelectedBank] = useState(null);

  return (
    <Container className="mt-5">
      <h2 className="mb-4 text-center">üè¶ Bank Loan Offers</h2>

      <Card className="p-4 shadow-lg">
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>Bank Name</th>
              <th>Interest Rate</th>
              <th>Max Loan Amount</th>
              <th>Loan Tenure</th>
              <th>Eligibility</th>
              <th>Apply</th>
            </tr>
          </thead>
          <tbody>
            {bankLoanOffers.map((bank, index) => (
              <tr key={index}>
                <td>{bank.bank}</td>
                <td>{bank.interestRate}</td>
                <td>{bank.maxLoan}</td>
                <td>{bank.tenure}</td>
                <td>{bank.eligibility}</td>
                <td>
                  <Button variant="primary" onClick={() => setSelectedBank(bank.bank)}>
                    Apply Now
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Card>

      {selectedBank && (
        <Card className="mt-4 p-3 text-center bg-success text-white">
          <h4>‚úÖ You selected {selectedBank} for your loan application.</h4>
          <p>Proceed with the application on the official bank website.</p>
        </Card>
      )}
    </Container>
  );
};

export default BankLoans;
