import React, { useState } from "react";
import axios from "axios";
import { Container, Form, Button, Card, Spinner } from "react-bootstrap";

function GeminiChat() {
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);
  const [language, setLanguage] = useState("en"); // Default language: English

  // Handle Chat Request
  const handleChat = async () => {
    if (!query.trim()) return;
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/chat", { query, language });
      setResponse(res.data.reply);
      speak(res.data.reply); // Speak response
    } catch (error) {
      setResponse("âŒ Error: Unable to fetch response.");
    }

    setLoading(false);
  };

  // Text-to-Speech (TTS) Function
  const speak = (text) => {
    if ("speechSynthesis" in window) {
      const speech = new SpeechSynthesisUtterance(text);
      speech.lang = language; // Set selected language for speech
      speech.rate = 1;
      speech.pitch = 1;
      window.speechSynthesis.speak(speech);
    }
  };

  return (
    <Container className="mt-4 d-flex flex-column align-items-center">
      <h2 className="mb-4">ðŸš— AutoSage Chatbot</h2>
      <Card className="p-4 shadow-lg w-75">
        {/* Language Selection */}
        <Form.Select className="mb-3" value={language} onChange={(e) => setLanguage(e.target.value)}>
          <option value="en">English</option>
          <option value="hi">Hindi</option>
          <option value="te">Telugu</option>
        </Form.Select>

        {/* User Input */}
        <Form.Control
          type="text"
          placeholder="Ask about vehicles..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="mb-3"
        />

        {/* Ask Button */}
        <Button onClick={handleChat} disabled={loading} variant="primary">
          {loading ? <Spinner animation="border" size="sm" /> : "Ask"}
        </Button>
      </Card>

      {/* Chatbot Response */}
      {response && (
        <Card className="mt-3 p-3 w-75">
          <strong>ðŸ”¹ Chatbot:</strong> {response}
        </Card>
      )}
    </Container>
  );
}

export default GeminiChat;
