import React, { useState } from "react";
import axios from "axios";
import { Container, Form, Button, Card, Spinner, ListGroup } from "react-bootstrap";

function GeminiChat() {
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);
  const [queryHistory, setQueryHistory] = useState([]); // ‚úÖ Store user queries

  const handleChat = async () => {
    if (!query.trim()) return;
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/chat", { query });
      const reply = res.data.reply;

      // ‚úÖ Update query history
      setQueryHistory([...queryHistory, { query, reply }]);
      setResponse(reply);
    } catch (error) {
      setResponse("‚ùå Error: Unable to fetch response.");
    }

    setLoading(false);
    setQuery(""); // ‚úÖ Clear input after sending
  };

  return (
    <Container className="mt-4 d-flex flex-column align-items-center">
      <h2 className="mb-4">üöó AutoSage Chatbot</h2>

      <Card className="p-4 shadow-lg w-75">
        <Form.Control
          type="text"
          placeholder="Ask about vehicles..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="mb-3"
        />
        <Button onClick={handleChat} disabled={loading} variant="primary">
          {loading ? <Spinner animation="border" size="sm" /> : "Ask"}
        </Button>
      </Card>

      {response && (
        <Card className="mt-3 p-3 w-75">
          <strong>üîπ Chatbot:</strong> {response}
        </Card>
      )}

      {/* ‚úÖ Show Query History */}
      {queryHistory.length > 0 && (
        <Card className="mt-4 p-3 w-75">
          <h5>üìù Query History</h5>
          <ListGroup>
            {queryHistory.map((item, index) => (
              <ListGroup.Item key={index}>
                <strong>üó®Ô∏è You:</strong> {item.query}  
                <br />
                <strong>ü§ñ Bot:</strong> {item.reply}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Card>
      )}
    </Container>
  );
}

export default GeminiChat;
