import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Container, Card, Button } from "react-bootstrap";
import { Link } from "react-router-dom";

function VehicleDetails() {
  const { id } = useParams(); // Get vehicle ID from URL
  const [vehicle, setVehicle] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/vehicles/${id}`)
      .then((response) => {
        setVehicle(response.data);
      })
      .catch((error) => {
        console.error("Error fetching vehicle details:", error);
      });
  }, [id]);

  if (!vehicle) {
    return <p>Loading vehicle details...</p>;
  }

  return (
    <Container className="mt-4">
      <Card>
        <Card.Img variant="top" src={vehicle.imageUrl} alt={vehicle.name} />
        <Card.Body>
          <Card.Title>{vehicle.name}</Card.Title>
          <Card.Text>
            <strong>Brand:</strong> {vehicle.brand} <br />
            <strong>Price:</strong> â‚¹{vehicle.price} <br />
            <strong>Fuel Type:</strong> {vehicle.fuelType} <br />
            <strong>Mileage:</strong> {vehicle.mileage} km/l <br />
            <strong>Engine:</strong> {vehicle.engine} <br />
            <strong>Transmission:</strong> {vehicle.transmission} <br />
            <strong>Features:</strong> {vehicle.features.join(", ")}
          </Card.Text>
          <Link to="/vehicles">
            <Button variant="secondary">Back to Vehicles</Button>
          </Link>
        </Card.Body>
      </Card>
    </Container>
  );
}

export default VehicleDetails;
