import React, { useState } from "react";
import axios from "axios";
import { Container, Form, Button, Card } from "react-bootstrap";

function GeminiChat() {
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChat = async () => {
    if (!query.trim()) return;
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/chat", { query });
      setResponse(res.data.reply);
    } catch (error) {
      console.error("Chatbot Error:", error);
      setResponse("Sorry, I couldn't process your request.");
    }

    setLoading(false);
  };

  return (
    <Container className="mt-4">
      <h2 className="text-center">Ask Our Vehicle Chatbot</h2>
      <Card className="p-3 shadow">
        <Form.Control
          type="text"
          placeholder="Ask about vehicles..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <Button className="mt-2" onClick={handleChat} disabled={loading}>
          {loading ? "Thinking..." : "Ask"}
        </Button>
      </Card>
      {response && (
        <Card className="mt-3 p-3">
          <strong>Chatbot:</strong> {response}
        </Card>
      )}
    </Container>
  );
}

export default GeminiChat;
