import React, { useState } from "react";
import { Container, Form, Button, Card } from "react-bootstrap";
import { Pie } from "react-chartjs-2";
import { Chart, ArcElement, Tooltip, Legend } from "chart.js";

Chart.register(ArcElement, Tooltip, Legend);

function EMICalculator() {
  const [price, setPrice] = useState("");
  const [downPayment, setDownPayment] = useState("");
  const [interestRate, setInterestRate] = useState("");
  const [tenure, setTenure] = useState("");
  const [emi, setEmi] = useState(null);
  const [totalPayable, setTotalPayable] = useState(null);
  const [totalInterest, setTotalInterest] = useState(null);

  const calculateEMI = () => {
    if (!price || !downPayment || !interestRate || !tenure) return;
    
    const loanAmount = price - downPayment;
    const monthlyRate = interestRate / 100 / 12;
    const months = tenure * 12;

    const emi =
      (loanAmount * monthlyRate * Math.pow(1 + monthlyRate, months)) /
      (Math.pow(1 + monthlyRate, months) - 1);

    const totalAmount = emi * months;
    const interestPaid = totalAmount - loanAmount;

    setEmi(emi.toFixed(2));
    setTotalPayable(totalAmount.toFixed(2));
    setTotalInterest(interestPaid.toFixed(2));
  };

  return (
    <Container className="mt-4 d-flex flex-column align-items-center">
      <h2 className="mb-4">üöó EMI & Loan Calculator</h2>
      <Card className="p-4 shadow-lg w-50">
        <Form.Group className="mb-3">
          <Form.Label>Vehicle Price (‚Çπ)</Form.Label>
          <Form.Control
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Down Payment (‚Çπ)</Form.Label>
          <Form.Control
            type="number"
            value={downPayment}
            onChange={(e) => setDownPayment(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Interest Rate (%)</Form.Label>
          <Form.Control
            type="number"
            value={interestRate}
            onChange={(e) => setInterestRate(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Loan Tenure (Years)</Form.Label>
          <Form.Control
            type="number"
            value={tenure}
            onChange={(e) => setTenure(e.target.value)}
          />
        </Form.Group>
        <Button onClick={calculateEMI} variant="primary">
          Calculate EMI
        </Button>
      </Card>

      {emi && (
        <Card className="mt-4 p-3 w-50">
          <h5>üìå EMI Breakdown</h5>
          <p>üí∞ Monthly EMI: ‚Çπ{emi}</p>
          <p>üíµ Total Amount Payable: ‚Çπ{totalPayable}</p>
          <p>üè¶ Total Interest Payable: ‚Çπ{totalInterest}</p>

          {/* Pie Chart */}
          <Pie
            data={{
              labels: ["Principal Amount", "Interest Amount"],
              datasets: [
                {
                  data: [totalPayable - totalInterest, totalInterest],
                  backgroundColor: ["#4CAF50", "#FF5733"],
                  hoverOffset: 4,
                },
              ],
            }}
          />
        </Card>
      )}
    </Container>
  );
}

export default EMICalculator;
